namespace Mirasvit\Stability\Controller\Adminhtml\Dashboard;

use Magento\Framework\Controller\ResultFactory;
use Mirasvit\Stability\Controller\Adminhtml\AbstractDashboard;

class Index extends AbstractDashboard
{
    /**
     * @return \Magento\Backend\Model\View\Result\Page
     */
    public function execute()
    {
        /** @var \Magento\Backend\Model\View\Result\Page $resultPage */
        $resultPage = $this->resultFactory->create(ResultFactory::TYPE_PAGE);

        $this->initPage($resultPage);

        $uiClass = str_replace('_', '-', $this->getRequest()->getParam('ui'));
        $resultPage->getConfig()->addBodyClass($uiClass);

        return $resultPage;
    }
}
